mapper.select:
  
    SELECT t.id, t.name, t.type, t.code, t.unit, t.num, t.remark FROM t_tgks_wms_currency t


        1=1
    
        AND t.ID = #{id}
    
    
        AND t.NAME = #{name}
    
    
        AND t.TYPE = #{type}
    
    
        AND t.CODE = #{code}
    
    
        AND t.UNIT = #{unit}
    
    
        AND t.NUM = #{num}
    
    
        AND t.REMARK = #{remark}
    


    ORDER BY ${sortSql}
